INSERT INTO `db_script_string` VALUES ('2000000001', 'Get in the Ring of Blood, $N. The fight is about to start!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000002', 'The battle is about to begin!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000003', '$N versus the ferocious clefthoof, Brokentoe!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000009', 'What happens?', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000010', 'Volcor need help ?', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000011', 'All right, I shall find people to help him.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000012', 'All right, let\'s go.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000013', 'Oh no! They a here!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000014', 'Oh, hello Grimclaw.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000015', 'Ok, $N. Now i can go the the Auberdine alone. Go to Terenthis. He waits for you. Thanks for the help.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000016', 'Ok, good luck friend. Thanks for the help.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000017', 'Ok, let\'s go!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000018', 'I can make it the rest of the way. $N. THANKS!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000019', 'Be patient $N. The torch is almost complete.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000020', 'That\'s interesting...', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000021', 'I need to consult Noram and Horatio.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000022', 'Look! Minions of Terokk are approaching!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000023', 'Look out! More are coming!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000024', 'And even more!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000025', 'An Avatar of Terokk! To arms!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000026', 'Whoops!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000029', 'Follow, $N. I will soon begin the summoning...', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000030', 'This makes sense!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000031', 'Your body is mine! Our fates are forever intertwined!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000032', 'Gird yourself $N, for the demon in this orb is a fel beast.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000033', 'Keep moving!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000034', 'Come, $N. Lord Stormrage awaits.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000035', 'Warriors of Dragonmaw, gather \'round! One among you has attained the rank of highlord! Bow your heads in reverence! Show your respect and allegiance to Highlord $N!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000036', 'Lord Illidan will be here shortly.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000037', 'All hail Lord Illidan!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000038', 'Lord Illidan, this is the Dragonmaw that I, and others, have told you about. He will lead us to victory!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000039', 'But... My lord, I do not understand. $N... He is the orc that has...', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000040', 'It will be done, my lord.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000041', 'So you thought to make a fool of Mor\'ghor, eh? Before you are delivered to Lord Illidan, you will feel pain that you could not know to exist. I will take pleasure in exacting my own vengeance.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000042', 'Until we meet again, brave one.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000043', 'begins a spell of summoning...', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000044', 'It is done...', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000045', 'There\'s nothing like some scalding mornbrew on a chilly Dun Morogh day to get things started right!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000046', 'Oooooo hot hot hot! If that won\'t put spring in your step, I don\'t know what will!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000047', 'Ribbit! No!! This cannot...ribbit...be! You have duped me with...ribbit...your foul trickery! Ribbit!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000048', 'Now let us place this rare earth in my planter...', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000049', 'Thank you again, $N. This rare earth will be very helpful in my experiments.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000050', 'Hello Grimclaw', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000051', 'What happens?', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000052', 'Volcor was gone?', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000053', 'You know where he is?', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000054', 'Well, ok. I am now sending people . We need find and help him.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000055', 'How many more of you grunts do I need to send back to mommy and daddy in a body bag before you grow a brain and realize that runnin\' head first into a Legion kill squad is suicide? And don\'t nod your thick skulls at me as if you know what I\'m talkin\' about!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000056', 'Hey there, Belm! Give me a mug of Thunder Ale, and one for my good friend $N.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000057', 'Spirits of Water, I give you praise, and I beg of you a favour. You have heard $N\'s pleas, and I trust his inent is noble. Please, will you aid us?', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000058', 'Thank you great spirit. Thank you!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000059', 'You are Dismissed, $N', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000060', 'By the stars! A spirit has been summoned!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000061', 'It\'s a mystery of the past indeed! But a key to our future!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000062', 'The threat be over! $N be savin\' us all!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000063', 'Follow me, good $C. I\'ll have your barding done faster than I could down a Dwarven stout. Mmmm... stout.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000064', 'Well, that should do it. Come now, back to the shop to finish our business!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000065', 'We will suffer no demon\'s servant in our lands!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000066', 'Peace and patience be with you, $N. Remain strong always.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000067', 'What are you doing in Ysera\'s realm, interloper? Leave us, I say! Your kind are not welcome here!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000068', 'You -- you\'re not one of them! Let\'s get out of sight before they find us! Hurry!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000069', 'They\'re following me, $R! Quickly, tell me why you\'re here before we\'re discovered! If they catch up to me, it will mean my death and yours!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000070', 'It would\'ve taken years to find the Book of the Raven without you, young sparrowhawk. Return to your home in Nagrand with our gratitude.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000071', 'This is it! This is really it -- the Book of the Raven!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000072', 'It\'s all here... how the raven\'s followers captured and subdued the sprints of rivals, trapping them forever within stone statues! The tablet even mentions the raven god\'s name -- Anzu. I\'d thought it was lost to history!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000073', 'There\'s something else here, too. After a discription of a bloody battle and the downfall of the raven, there\'s a prophecy. Anzu will haunt the dreams of his enemies, twisting them to affect a return to this world...', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000074', 'The tablet has the confirmation we need, just as I thought. Now, we have to use this information to draw out and defeat this Anzu before he can do any more damage to the Emerald Dream!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000075', 'Since the sparrowhawks have proven such a success in hunting down the raven stones, Watcher Elaira has trained another one to relay my voice at a great distance. He can help us communicate as you investigate the bird spirits revealed in the tablet!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000000076', 'Hear ye, denizens of Tanaris! Let it be known $n is an exalted member of the Mithril Order. A blacksmith of honor, dedication, and infinite patience. Three cheers for $n!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005000', 'Poor Dorius. If I ever get my hands on those Dark Irons, so help me...', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005001', 'Opus wants some Cuergo\'s Gold... with worm.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005002', 'Bijou lost! SQUAWK! *cough', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005003', 'Kibler is being investigated by G.E.T.A.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005004', 'Bijou knows where treasures are hidden.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005005', 'Dragons controlling orcs!? SQUAWK!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005006', 'They all go to the Spire but none never come back... just like Bijou.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005007', 'Ask Kibler about the treasure! SQUAWK!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005008', 'SQUAWK!! *cough* *cough* I need to quit smoking!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005009', 'Well done, soldiers! At ease!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005010', 'Zug zug! Happy to help!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005011', 'Yes right away momma!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005012', 'Mysterious hoofprints, scorched shields, stray badges... how do we make sense of all this?', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005013', 'I don\'t think Drazzit\'s going to have any luck finding anyone to kill this raptor. I mean, he\'s already raised the reward twice...', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005014', 'But this the only building that fit Brogg! Goblins make buildings too small!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005015', 'I thought we talked about this, Brogg. You can\'t stay in this building. Sure, it can house you, but there\'s no room for anyone else!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005016', 'Yes, we said you can stay here, but this building is our headquarters! All of our manuals and blueprints are stored here!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005017', 'I\'m inclined to say they made ogres too big! I can see I\'m not going to convince you to move now, but don\'t think this is permanent!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005018', 'Ah, this must be him now... no?', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005019', 'You might wanna stand back. Fish guttin\' is a dirty job.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005020', 'It\'s good to see you defending Theramore with pride, friend.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005021', 'When you\'re off duty, let\'s go to the inn. I\'ll buy a round and we can reminisce about the good old days.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005022', 'Wouldn\'t you like to bring those days back? I have some friends who know just the way to do that.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005023', 'Every item that I have for sale I acquired myself. You have my personal guarantee of authenticity.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005024', 'It\'s getting away!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005025', 'Prizes! Get your prizes right here! Get over here and turn in your Darkmoon Faire Prize Tickets for valuable prizes!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005026', 'Come speak with me, and what once was cloudy shall become crystal clear.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005027', 'The Lich King\'s forces are building. It is imperative that our timetable supports his plans.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005028', 'Tomorrow we will begin training of our promising dragons, so don\'t forget your chew toys.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005029', 'Our oldest clutch of dragons are still far from maturity, but with patience and study, we are confident the dragons will soon be ready.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005030', 'If you wait to come see me, there may be nothing left!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005031', 'I come from the land down under... where women plague and men sunder.', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005032', '...And then the tauren said, \"13 INCHES!\"', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005033', 'Come one, come all! Welcome to the Darkmoon Faire! Do you crave adventure? Do you seek exotic and mysterious treasures? Then look no further! You, my friend, have come to the right place! Dive right in and take part in all that the Faire has to offer! We\'ll be at this location all week, so be sure to tell your friends and loved ones!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005034', 'Welcome one and all to the Darkmoon Faire, the greatest event in all the worlds! We have it all... delicious food, strong drinks, exotic artifacts, fortunes read, amazing prizes and excitement without end! Don\'t forget to turn in your Darkmoon Faire Prize Tickets to Gelvas Grimegate! All it takes is five or more and you\'re on your way to the most wondrous prizes in all of Outland. Everybody is a winner', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005035', 'It\'s getting away!', null, null, null, null, null, null, null, null);
INSERT INTO `db_script_string` VALUES ('2000005036', 'Please! I must feed on something soon... A mana crystal... a shard... anything! The pain is unbearable!', null, null, null, null, null, null, null, null);
